import React, { useState,useContext } from "react";
import {BoardContext} from './Board' 
import { Card} from "react-bootstrap";
import TaskForm from './New Task/TaskForm'
const formateDate = (date) => {
  var months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  var month = months[date.getMonth()];
  var day = "" + date.getDate();

  if (day.length < 2) day = "0" + day;

  return month + " " + day + ", " + date.getFullYear();
};

const editedValues = {
  title: "ABCD",
  description: "sdsdsd",
  startDate: new Date(),
  endDate: new Date(),
};

function CardItem(props) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const {onDeletingTask,onUpdatingTask} = useContext(BoardContext);
  const handleShow = () => {
    setShow(true);
    //setReset(true)
  };

  const clickHandler = (type) => {
    if (type === "edit") {
      handleShow();
    } else if (type === "delete") {
      onDeletingTask(props.task.id)
    }
  };

  const handleUpdate = () => {

    onUpdatingTask(2)
  };

  return (
    <>
      <TaskForm editedValues={editedValues} taskState='Update' show={show} handleClose={handleClose} onSubmit={handleUpdate}></TaskForm>
      <Card key={props.task.id} className="card-task">
        <Card.Body>
          <Card.Title>
            {props.task.title}{" "}
            <div className="card-task-option pull-right">
              <a onClick={() => clickHandler("edit")}>
                <i className="fas fa-edit"></i>
              </a>
              &nbsp;
              <a onClick={() => clickHandler("delete")}>
                <i className="fas fa-trash"></i>
              </a>
            </div>
          </Card.Title>

          <table>
            <tbody>
              <tr>
                <td className="font-weight-bold">Start Date:</td>
                <td>{formateDate(props.task.startDate)}</td>
              </tr>
              <tr>
                <td className="font-weight-bold">Due Date:</td>
                <td className="pull-right">
                  {formateDate(props.task.endDate)}
                </td>
              </tr>
            </tbody>
          </table>
        </Card.Body>
      </Card>
    </>
  );
}

export default CardItem;
