import React, { useState, useEffect } from "react";
import { Button, Modal, FormGroup, FormLabel } from "react-bootstrap";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import "./NewTask.css";

const validationSchema = Yup.object({
  title: Yup.string().required("Required"),
  description: Yup.string().required("Required"),
  startDate: Yup.string().required("Required"),
  endDate: Yup.string().required("Required"),
});
const initialValues = {
  title: "",
  description: "",
  startDate: new Date(2020,6,1),
  endDate: new Date(2020,6,20),
};

function NewTask(props) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => {
    setShow(true);
    //setReset(true)
  };

  useEffect(() => {}, []);
  const onSubmit = (values, submitProps) => {
    console.log("Form data", values);
    console.log("submitProps", submitProps);
    submitProps.setSubmitting(false);
    props.addNewTask(values);
    //submitProps.resetForm();
  };
  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        New Task
      </Button>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={onSubmit}
        enableReinitialize
      >
        {(formik) => {
          return (
            <Modal show={show} onHide={handleClose}>
              <Modal.Header closeButton>
                <Modal.Title>New Task</Modal.Title>
              </Modal.Header>
              <Form>
                <Modal.Body>
                  <FormGroup>
                    <FormLabel>Title</FormLabel>
                    <Field
                      type="text"
                      id="title"
                      name="title"
                      className={`form-control ${
                        formik.touched.title && formik.errors.title ? "is-invalid" : ""
                      }`}
                    />
                     <ErrorMessage
                      component="div"
                      name="title"
                      className="invalid-feedback"
                    />
                  </FormGroup>
                  <FormGroup>
                    <FormLabel>Description</FormLabel>
                    <Field
                      type="text"
                      id="description"
                      name="description"
                      className={`form-control ${
                        formik.touched.description && formik.errors.description ? "is-invalid" : ""
                      }`}
                      as="textarea"
                    />
                    <ErrorMessage
                      component="div"
                      name="description"
                      className="invalid-feedback"
                    />
                  </FormGroup>
                  <FormGroup>
                    <FormLabel>Title</FormLabel>
                    <Field
                      type="text"
                      id="startDate"
                      name="startDate"
                      className={`form-control ${
                        formik.touched.startDate && formik.errors.startDate ? "is-invalid" : ""
                      }`}
                    />
                    <ErrorMessage
                      component="div"
                      name="startDate"
                      className="invalid-feedback"
                    />
                  </FormGroup>
                  <FormGroup>
                    <FormLabel>Title</FormLabel>
                    <Field
                      type="text"
                      id="endDate"
                      name="endDate"
                      className={`form-control ${
                        formik.touched.endDate && formik.errors.endDate ? "is-invalid" : ""
                      }`}
                    />
                     <ErrorMessage
                      component="div"
                      name="endDate"
                      className="invalid-feedback"
                    />
                  </FormGroup>
                </Modal.Body>
                <Modal.Footer>
                  <Button variant="secondary" onClick={handleClose}>
                    Close
                  </Button>
                  <Button variant="primary" type="submit">
                    Save Changes
                  </Button>
                </Modal.Footer>
              </Form>
            </Modal>
          );
        }}
      </Formik>
    </>
  );
}

export default NewTask;
